{!REQUIRESCRIPT("/soap/ajax/24.0/apex.js")} 

var readyToProcess = ""; 
var countAssets = sforce.connection.query("SELECT COUNT() FROM Asset WHERE Active__c = true AND AccountId = '{!Addendum__c.AccountId__c}' AND Contract_Type__c != 'Month-to-month' AND Id NOT IN (SELECT Asset__c FROM Addendum_Line_Item__c WHERE Addendum__c = '{!Addendum__c.Id}')"); 

if(countAssets.size > 0 && "{!Addendum__c.CoTerm_Change__c}" == "CoTerm") 
			{ 
				readyToProcess += "When changing the renewal date, all coterminous products must also be changed. Please adjust the current modifications or add all coterminous products to the addendum. "; 
			} 
			if("{!Addendum__c.CoTerm_Dates__c}" != 1) 
			{ 
				readyToProcess += "The new renewal date must be the same for all products. Please adjust the current modifications. "; 
			} 
			if("{!Addendum__c.Addendum_Signer__c}" == "") 
			{ 
				readyToProcess += "Please select an Addendum Signer. "; 
			}  

if(readyToProcess == "") 
{ 
var newAddendum= new sforce.SObject("Addendum__c"); 
newAddendum.id = "{!Addendum__c.Id}"; 
newAddendum.OwnerId="{!Addendum__c.QueueID__c}"; 
newAddendum.Status__c="Ready for Processing"; 

var result = sforce.connection.update([newAddendum]); 

if (result[0].getBoolean("success")) 
{ 
// Refresh window 
window.location.reload(); 
} 
else 
{ 
alert("something broke"); 
} 
} 
else 
{ 
alert(readyToProcess); 
}
