var getTabClass = function() {
    var bodytags = document.getElementsByTagName("body");
    var classes  = bodytags[0].className.split(' ');
    var hostname = window.location.hostname;
    try {
        try {
            sforce.connection.sessionId="{!$Api.Session_ID}";
        } catch (e) {
            //
        }
        user  = sforce.connection.getUserInfo();
        var theme = user.userUiSkin;
        return {
            "css_base"  : "https://"+window.location.hostname,
            "css_theme" : theme,
            "css_class" : classes[0]};
    } catch (e) {
        return {
            "css_base" : "https://"+window.location.hostname,
            "css_class" : classes[0],
            "css_theme" : false
        };
    }
};
var getTabClassString = function() {
    var css_info = getTabClass();
    var tmp_url = "";
    tmp_url += '&css_base='+encodeURIComponent(css_info.css_base);
    if (css_info.css_theme) {
        tmp_url += '&css_theme='+encodeURIComponent(css_info.css_theme);
    }
    tmp_url += '&css_class='+encodeURIComponent(css_info.css_class);
    return tmp_url;
};

var openPopup = function() {
    pd.requestSalesforceInstance(sforce.connection.sessionId);
    pd.makeRequest('getAuthToken',
        function (result) {
            var jwt = typeof result == 'object' && 'token' in result ? result.token : '';
            var use_jwt = jwt != '';
            var html = "<form name='createList' method='post' action='https://{!$User.qbdialer__is_subdomain__c}.insidesales.com/noauth/create_sf_list?";
            html += "&salesforce_package_version=6.36.21";
            html += "&type=Account";
            if (!use_jwt) {
                html += "&organization_id={!$Organization.Id}";
                html += "&token={!User.qbdialer__is_token__c}";
            }
            html += "&agent_phone={!User.Phone}";
            html += getTabClassString();
            html += "'><input type='submit' style='visibility:hidden'>"; 
            if (use_jwt) {
                html += "<input type='hidden' name='Authorization' value='"+jwt+"'>";
            }
            var data = "";
            for (var i=0; i < idArray.length; i++) {
                data = {Id:idArray[i]};
                data = JSON.stringify(data);
                html += "<input type='hidden' name='leads[]' value='"+data+"'>";
            }
            html += "<"+"/form>";
            html += "<script>document.createList.submit();<"+"/script>";
            var host = document.location.protocol+"//"+
            document.location.hostname;
            var w = window.open(host, "_blank", "height=700,width=800,menubar=0,resizable=1,scrollbars=1,status=0,titlebar=0,toolbar=0,location=0");
            w.document.write(html);
        }
    );
}

var user_oauth_token = '{!JSENCODE($User.qbdialer__is_token__c)}';
if (!user_oauth_token) {
    window.location.href="/apex/qbdialer__InsideSales_OAuth_Setup";
} else {
    var idArray = {!GETRECORDIDS($ObjectType.Contact)};
    if (idArray.length == 0) {
        alert("Please select at least one row");
    } else {
        openPopup();
    }
}
